/* eslint-disable @next/next/no-img-element */
"use client"
import { SearchOutlined, } from '@ant-design/icons';
import React, { useRef, useState, useEffect } from 'react';
import Highlighter from 'react-highlight-words';
import type { InputRef } from 'antd';
import { Button, Image, Input, Space, Table, Descriptions, Badge, Divider } from 'antd';
import type { ColumnType, ColumnsType } from 'antd/es/table';
import type { FilterConfirmProps } from 'antd/es/table/interface';
import { useAppDispatch, useAppSelector } from "@/store/hooks";
import { Order, OrderProduct, Product } from '@/store/types';
import { Typography } from 'antd';
import { fetchUserOrdersAsync } from '@/store/action/order/orderSlice';

const { Text } = Typography;

type DataIndex = keyof Order;

const OrdersTable = () => {
    const [searchText, setSearchText] = useState('');
    const [searchedColumn, setSearchedColumn] = useState('');
    const searchInput = useRef<InputRef>(null);
    const dispatch = useAppDispatch();
    const { orders, loading } = useAppSelector(state => state.orders);
    var data: Order[] = orders;


    useEffect(() => {
        dispatch(fetchUserOrdersAsync())
    }, [dispatch]);

    const handleSearch = (
        selectedKeys: string[],
        confirm: (param?: FilterConfirmProps) => void,
        dataIndex: DataIndex,
    ) => {
        confirm();
        setSearchText(selectedKeys[0]);
        setSearchedColumn(dataIndex);
    };

    const handleReset = (clearFilters: () => void) => {
        clearFilters();
        setSearchText('');
    };

    const getColumnSearchProps = (dataIndex: DataIndex): ColumnType<Order> => ({
        filterDropdown: ({ setSelectedKeys, selectedKeys, confirm, clearFilters, close }) => (
            <div style={{ padding: 8 }} onKeyDown={(e) => e.stopPropagation()}>
                <Input
                    ref={searchInput}
                    placeholder={`Search ${dataIndex}`}
                    value={selectedKeys[0]}
                    onChange={(e) => setSelectedKeys(e.target.value ? [e.target.value] : [])}
                    onPressEnter={() => handleSearch(selectedKeys as string[], confirm, dataIndex)}
                    style={{ marginBottom: 8, display: 'block' }}
                />
                <Space>
                    <Button
                        type="primary"
                        onClick={() => handleSearch(selectedKeys as string[], confirm, dataIndex)}
                        icon={<SearchOutlined />}
                        size="small"
                        style={{ width: 90 }}
                        className='bg-[#876553]'
                    >
                        Search
                    </Button>
                    <Button
                        onClick={() => clearFilters && handleReset(clearFilters)}
                        size="small"
                        style={{ width: 90 }}
                    >
                        Reset
                    </Button>
                    <Button
                        type="link"
                        size="small"
                        onClick={() => {
                            confirm({ closeDropdown: false });
                            setSearchText((selectedKeys as string[])[0]);
                            setSearchedColumn(dataIndex);
                        }}
                    >
                        Filter
                    </Button>
                    <Button
                        type="link"
                        size="small"
                        onClick={() => {
                            close();
                        }}
                    >
                        close
                    </Button>
                </Space>
            </div>
        ),
        filterIcon: (filtered: boolean) => (
            <SearchOutlined style={{ color: filtered ? '#1677ff' : undefined }} />
        ),
        onFilter: (value, record: any) =>
            record[dataIndex]
                .toString()
                .toLowerCase()
                .includes((value as string).toLowerCase()),
        onFilterDropdownOpenChange: (visible) => {
            if (visible) {
                setTimeout(() => searchInput.current?.select(), 100);
            }
        },
        render: (text) =>
            searchedColumn === dataIndex ? (
                <Highlighter
                    highlightStyle={{ backgroundColor: '#ffc069', padding: 0 }}
                    searchWords={[searchText]}
                    autoEscape
                    textToHighlight={text ? text.toString() : ''}
                />
            ) : (
                text
            ),
    });

    const columns: ColumnsType<Order> = [
        {
            title: "Order Details",
            dataIndex: 'products',
            // key: 'products._id',
            render: (text: OrderProduct[]) => (
                <div>
                    <Descriptions size="small" layout="vertical" bordered items={[{
                        label: 'Detail',
                        children: (
                            <>
                                {text.map((product: OrderProduct, index: number) => {
                                    return (<div key={index} >
                                        <div className='flex flex-row'>

                                            <Image

                                                width={50}
                                                height={50}
                                                src={`${product.productImage[0]}`}
                                                alt={product.title}
                                                fallback="data:image/png;base64,"
                                            />
                                            <div className='flex flex-col pl-1'>
                                                <span>
                                                    {product.title}
                                                </span>
                                                <span>
                                                    Quantity: {product.quantity}
                                                </span>
                                            </div>
                                        </div>
                                        <Divider />
                                    </div>)
                                })}
                            </>
                        ),
                    }]} />
                </div>
            )
        },
        {
            title: 'Address',
            dataIndex: 'shipping',
            ...getColumnSearchProps('shipping'),
            render: (text) => (
                <Text>{Object.keys(text.address).map((key) => {
                    return text.address[key] + ', '; // The return value is not used, map is used here for iteration
                })}</Text>
            )
        },

        {
            title: 'Order Date',
            dataIndex: 'createdAt',
            ...getColumnSearchProps('createdAt'),
            render: (text) => (
                <Text>{new Date(text).toLocaleDateString('en-CA')}</Text>
            )

        },

        {
            title: 'Total',
            dataIndex: 'total',
            ...getColumnSearchProps('total'),
            sorter: (a, b) => a.total - b.total,
            sortDirections: ['descend', 'ascend'],
            render: (text) => (
                <Text strong>${text / 100}</Text>
            )

        },
        {
            title: 'Delivery Status',
            dataIndex: 'delivery_status',
            ...getColumnSearchProps('delivery_status'),
            sortDirections: ['descend', 'ascend'],
            render: (text) => (

                <Badge status="processing" text={text} />
            )


        },

    ];

    return (
        <>
            <Table columns={columns} dataSource={data} rowKey="_id" loading={loading} className='py-5' />
        </>
    );
};

export default OrdersTable;